'use strict';

module.exports = function(grunt) {

	// Please see the Grunt documentation for more information regarding task
	// creation: http://gruntjs.com/creating-tasks

	grunt.registerMultiTask('insert', 'Insert code from one file into another.', function() {
		// Merge task-specific and/or target-specific options with these defaults.
		var options = this.options({
				removeComments: true
		});

		// Iterate over all specified file groups.
		this.files.forEach(function(f) {

			// Read source file
			var src = f.src.filter(function(filepath) {
				// Warn on and remove invalid source files (if nonull was set).
				if (!grunt.file.exists(filepath)) {
					grunt.log.warn('Source file "' + filepath + '" not found.');
					return false;
				} else {
					return true;
				}
			}).map(function(filepath) {
				return grunt.file.read(filepath); // Read file source.
			});

			var files = grunt.file.expand({}, f.dest);

			for (var i = 0, l = files.length; i < l; i++) {

				// Read destination file
				if (!grunt.file.exists(files[i])) {
					grunt.log.warn('Destination file "' + files[i] + '" not found.');
				} else {
					var dest = grunt.file.read(files[i]); // Read file source.
				}

				var result = dest.replace(f.match,src);

				// Write the temporary destination file.
				grunt.file.write(files[i]+"_tmp", result);

				// overwrite the original with the temporary file
				grunt.file['delete'](files[i]);
				grunt.file.copy(files[i]+"_tmp", files[i]);
				grunt.file['delete'](files[i]+"_tmp");

				// Print a success message.
				var input = f.src.toString().replace(/.*\//,""); // Remove path
				var output = files[i].toString().replace(/.*\//,""); // Remove path
				grunt.log.writeln("Injected " + input + " into " + output);
			}
		});
	});

};
